<?php/* WP-PageNavi */function jnn_round($num, $tonearest) {   return floor($num/$tonearest)*$tonearest;}/** * Change the function name, minimalize the conflict when user define to use the original plugin :) */function oi_pagenavi() {	global $wpdb, $wp_query;	$befored = '';	$aftered = '';	if (!is_single()) {		$request = $wp_query->request;		$posts_per_page = intval(get_query_var('posts_per_page'));		$paged = intval(get_query_var('paged'));		$numposts = $wp_query->found_posts;		$max_page = $wp_query->max_num_pages;		if(empty($paged) || $paged == 0) {			$paged = 1;		}		$pages_to_show = intval(5);		$larger_page_to_show = intval(3);		$larger_page_multiple = intval(10);		$pages_to_show_minus_1 = $pages_to_show - 1;		$half_page_start = floor($pages_to_show_minus_1/2);		$half_page_end = ceil($pages_to_show_minus_1/2);		$start_page = $paged - $half_page_start;		if($start_page <= 0) {			$start_page = 1;		}		$end_page = $paged + $half_page_end;		if(($end_page - $start_page) != $pages_to_show_minus_1) {			$end_page = $start_page + $pages_to_show_minus_1;		}		if($end_page > $max_page) {			$start_page = $max_page - $pages_to_show_minus_1;			$end_page = $max_page;		}		if($start_page <= 0) {			$start_page = 1;		}		$larger_per_page = $larger_page_to_show*$larger_page_multiple;		$larger_start_page_start = (jnn_round($start_page, 10) + $larger_page_multiple) - $larger_per_page;		$larger_start_page_end = jnn_round($start_page, 10) + $larger_page_multiple;		$larger_end_page_start = jnn_round($end_page, 10) + $larger_page_multiple;		$larger_end_page_end = jnn_round($end_page, 10) + ($larger_per_page);		if($larger_start_page_end - $larger_page_multiple == $start_page) {			$larger_start_page_start = $larger_start_page_start - $larger_page_multiple;			$larger_start_page_end = $larger_start_page_end - $larger_page_multiple;		}		if($larger_start_page_start <= 0) {			$larger_start_page_start = $larger_page_multiple;		}		if($larger_start_page_end > $max_page) {			$larger_start_page_end = $max_page;		}		if($larger_end_page_end > $max_page) {			$larger_end_page_end = $max_page;		}		if($max_page > 1) {		//	$pages_text = str_replace("%CURRENT_PAGE%", number_format_i18n($paged), addslashes("Page %CURRENT_PAGE% of %TOTAL_PAGES%") );			//$pages_text = str_replace("%TOTAL_PAGES%", number_format_i18n($max_page), $pages_text);			echo $befored.'<div class="wega-pagenavi">'."\n";					if(!empty($pages_text)) {						echo '<span class="pages">'.$pages_text.'</span>';					}					if ($start_page >= 2 && $pages_to_show < $max_page) {						$first_page_text = str_replace("%TOTAL_PAGES%", number_format_i18n($max_page), addslashes("&laquo; First") );						echo '<a href="'.clean_url(get_pagenum_link()).'" class="first" title="'.$first_page_text.'">'.$first_page_text.'</a>';							echo '<span class="extend">...</span>';					}					if($larger_page_to_show > 0 && $larger_start_page_start > 0 && $larger_start_page_end <= $max_page) {						for($i = $larger_start_page_start; $i < $larger_start_page_end; $i+=$larger_page_multiple) {							$page_text = str_replace("%PAGE_NUMBER%", number_format_i18n($i), addslashes("%PAGE_NUMBER%") );							echo '<a href="'.clean_url(get_pagenum_link($i)).'" class="page" title="'.$page_text.'">'.$page_text.'</a>';						}					}					previous_posts_link( addslashes('&laquo;') );					for($i = $start_page; $i  <= $end_page; $i++) {												if($i == $paged) {							$current_page_text = str_replace("%PAGE_NUMBER%", number_format_i18n($i), addslashes("%PAGE_NUMBER%") );							echo '<span class="current">'.$current_page_text.'</span>';						} else {							$page_text = str_replace("%PAGE_NUMBER%", number_format_i18n($i), addslashes("%PAGE_NUMBER%") );							echo '<a href="'.clean_url(get_pagenum_link($i)).'" class="page" title="'.$page_text.'">'.$page_text.'</a>';						}					}					next_posts_link( addslashes('&raquo;'), $max_page);					if($larger_page_to_show > 0 && $larger_end_page_start < $max_page) {						for($i = $larger_end_page_start; $i <= $larger_end_page_end; $i+=$larger_page_multiple) {							$page_text = str_replace("%PAGE_NUMBER%", number_format_i18n($i), addslashes("%PAGE_NUMBER%") );							echo '<a href="'.clean_url(get_pagenum_link($i)).'" class="page" title="'.$page_text.'">'.$page_text.'</a>';						}					}					if ($end_page < $max_page) {						echo '<span class="extend">...</span>';						$last_page_text = str_replace("%TOTAL_PAGES%", number_format_i18n($max_page), addslashes("Last &raquo;") );						echo '<a href="'.clean_url(get_pagenum_link($max_page)).'" class="last" title="'.$last_page_text.'">'.$last_page_text.'</a>';					}			echo '</div>'.$aftered."\n";			echo '<div class="clear"></div>';		}	}}?>